import React, { Component } from 'react'

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      hours: 0,
      minutes:0,
      seconds: 0,
    }
  }

  componentDidMount() {
    setInterval(() => {
      this.setState(({ time }) => ({time: time + 1}))
    }, 1000)
  }

  render() {
    const { hours, minutes, seconds } = this.state;
    return (
      <div>
        <h1>{ hours }: </h1>
        <h1>{ minutes }: </h1>
        <h1>{ seconds }</h1>
      </div>
    )
  }
}
