import React, { Component } from 'react'

export default class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      timeWork: 0,
      pausaCurta: 0,
      pausaLonga: 0,
      ciclos: 0,
    }
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    const key = event.target.name;
    this.setState({ [key]: event.target.value })
  }

  render() {
    const { timeWork, pausaCurta, pausaLonga, ciclos } = this.state;
    return (
      <form>
        <label htmlFor="work">
          Tempo Focado:
          <input 
            value={ timeWork }
            type="number"
            id="work"
            name="timeWork" 
            onChange={ this.handleChange }
          />
        </label>
        <label htmlFor="pausa">
          Pausa curta(minutos):
          <input
            value={ pausaLonga } 
            type="number"
            id="pausaLonga"
            name="pausaLonga"
            onChange={ this.handleChange } 
          />
        </label>
        <label htmlFor="pausa">
          Pausa longa(minutos):
          <input
            value={ pausaCurta } 
            type="number"
            id="pausaCurta"
            name="pausaCurta"
            onChange={ this.handleChange } 
          />
        </label>
        <label htmlFor="ciclos">
          Número de ciclos:
          <input
            value={ ciclos } 
            type="number"
            id="ciclos"
            name="ciclos"
            onChange={ this.handleChange } 
          />
        </label>
        <button type="button">Começar</button>
      </form>
    )
  }
}
