const INITIAL_STATE = {
  timeWork: 0,
  pausaCurta: 0,
  pausaLonga: 0,
}

const GET_INFO_TIME = 'GET_INFO_TIME';

const time = (state = INITIAL_STATE, action) => {
  switch(action.type) {
    case GET_INFO_TIME:
      return {
        ...state,
        time: action.payload,
      }
    default:
      return state;
  }
}

export default time;
