import React, { Component } from 'react'

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      time: 0,
    }
  }

  render() {
    return (
      <div>
        { () => {
          setInterval(() => {
            this.setState(({ time }) => ({ time: time + 1 }));
            return <h1>{ this.state.time }</h1>;
          }, 1000);
        } }
      </div>
    )
  }
}
