import React, { Component } from 'react'
import Cronometro from './Cronometro'
import Form from './Form'

export default class HomePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      ligado: false,
    }

    this.ligaDesliga = this.ligaDesliga.bind(this);
  }

  ligaDesliga() {
    this.setState((prevState) => ({ ligado: !prevState.ligado }));
  }

  render() {
    const { ligado } = this.state;
    return (
      <div>
        <audio preload="metadata" controls autoPlay>
          <source src="midia/music.mp3" type="audio/mp3"></source>
          <p>não foi possível carregar</p>
        </audio>
        <Form />
        { ligado ? <Cronometro /> : '' }
        <button 
          type="button"
          onClick={ this.ligaDesliga }
        >
          { ligado ? 'Desligar' : 'Ligar' }
        </button>
      </div>
    )
  }
}
